	 * @return     void
	 */
	public function setByPosition($pos, $value)
	{
		switch($pos) {
			case 0:
				$this->setObject($value);
				break;
			case 1:
				$this->setMetaTitle($value);
				break;
			case 2:
				$this->setMetaDescription($value);
				break;
			case 3:
				$this->setMetaKeywords($value);
				break;
			case 4:
				$this->setJavascript($value);
				break;
			case 5:
				$this->setManualkeyword($value);
				break;
			case 6:
				$this->setExcludesitemap($value);
				break;
			case 7:
				$this->setExcludecrawler($value);
				break;
			case 8:
				$this->setPriority($value);
				break;
			case 9:
				$this->setId($value);
				break;
		} // switch()
	}

	/**
	 * Populates the object using an array.
	 *
	 * This is particularly useful when populating an object from one of the
	 * request arrays (e.g. $_POST).  This method goes through the column
	 * names, checking to see whether a matching key exists in populated
	 * array. If so the setByName() method is called for that column.
	 *
	 * You can specify the key type of the array by additionally passing one
	 * of the class type constants BasePeer::TYPE_PHPNAME, BasePeer::TYPE_STUDLYPHPNAME,
	 * BasePeer::TYPE_COLNAME, BasePeer::TYPE_FIELDNAME, BasePeer::TYPE_NUM.
	 * The default key type is the column's phpname (e.g. 'AuthorId')
	 *
	 * @param      array  $arr     An array to populate the object from.
	 * @param      string $keyType The type of keys the array uses.
	 * @return     void
	 */
	public function fromArray($arr, $keyType = BasePeer::TYPE_PHPNAME)
	{
		$keys = PropertiesPeer::getFieldNames($keyType);

		if (array_key_exists($keys[0], $arr)) $this->setObject($arr[$keys[0]]);
		if (array_key_exists($keys[1], $arr)) $this->setMetaTitle($arr[$keys[1]]);
		if (array_key_exists($keys[2], $arr)) $this->setMetaDescription($arr[$keys[2]]);
		if (array_key_exists($keys[3], $arr)) $this->setMetaKeywords($arr[$keys[3]]);
		if (array_key_exists($keys[4], $arr)) $this->setJavascript($arr[$keys[4]]);
		if (array_key_exists($keys[5], $arr)) $this->setManualkeyword($arr[$keys[5]]);
		if (array_key_exists($keys[6], $arr)) $this->setExcludesitemap($arr[$keys[6]]);
		if (array_key_exists($keys[7], $arr)) $this->setExcludecrawler($arr[$keys[7]]);
		if (array_key_exists($keys[8], $arr)) $this->setPriority($arr[$keys[8]]);
		if (array_key_exists($keys[9], $arr)) $this->setId($arr[$keys[9]]);
	}

	/**
	 * Build a Criteria object containing the values of all modified columns in this object.
	 *
	 * @return     Criteria The Criteria object containing all modified values.
	 */
	public function buildCriteria()
	{
		$criteria = new Criteria(PropertiesPeer::DATABASE_NAME);

		if ($this->isColumnModified(PropertiesPeer::OBJECT)) $criteria->add(PropertiesPeer::OBJECT, $this->object);
		if ($this->isColumnModified(PropertiesPeer::META_TITLE)) $criteria->add(PropertiesPeer::META_TITLE, $this->meta_title);
		if ($this->isColumnModified(PropertiesPeer::META_DESCRIPTION)) $criteria->add(PropertiesPeer::META_DESCRIPTION, $this->meta_description);
		if ($this->isColumnModified(PropertiesPeer::META_KEYWORDS)) $criteria->add(PropertiesPeer::META_KEYWORDS, $this->meta_keywords);
		if ($this->isColumnModified(PropertiesPeer::JAVASCRIPT)) $criteria->add(PropertiesPeer::JAVASCRIPT, $this->javascript);
		if ($this->isColumnModified(PropertiesPeer::MANUALKEYWORD)) $criteria->add(PropertiesPeer::MANUALKEYWORD, $this->manualkeyword);
		if ($this->isColumnModified(PropertiesPeer::EXCLUDESITEMAP)) $criteria->add(PropertiesPeer::EXCLUDESITEMAP, $this->excludesitemap);
		if ($this->isColumnModified(PropertiesPeer::EXCLUDECRAWLER)) $criteria->add(PropertiesPeer::EXCLUDECRAWLER, $this->excludecrawler);
		if ($this->isColumnModified(PropertiesPeer::PRIORITY)) $criteria->add(PropertiesPeer::PRIORITY, $this->priority);
		if ($this->isColumnModified(PropertiesPeer::ID)) $criteria->add(PropertiesPeer::ID, $this->id);

		return $criteria;
	}

	/**
	 * Builds a Criteria object containing the primary key for this object.
	 *
	 * Unlike buildCriteria() this method includes the primary key values regardless
	 * of whether or not they have been modified.
	 *
	 * @return     Criteria The Criteria object containing value(s) for primary key(s).
	 */
	public function buildPkeyCriteria()
	{
		$criteria = new Criteria(PropertiesPeer::DATABASE_NAME);

		$criteria->add(PropertiesPeer::ID, $this->id);

		return $criteria;
	}

	/**
	 * Returns the primary key for this object (row).
	 * @return     int
	 */
	public function getPrimaryKey()
	{
		return $this->getId();
	}

	/**
	 * Generic method to set the primary key (id column).
	 *
	 * @param      int $key Primary key.
	 * @return     void
	 */
	public function setPrimaryKey($key)
	{
		$this->setId($key);
	}

	/**
	 * Sets contents of passed object to values from current object.
	 *
	 * If desired, this method can also make copies of all associated (fkey referrers)
	 * objects.
	 *
	 * @param      object $copyObj An object of Properties (or compatible) type.
	 * @param      boolean $deepCopy Whether to also copy all rows that refer (by fkey) to the current row.
	 * @throws     PropelException
	 */
	public function copyInto($copyObj, $deepCopy = false)
	{

		$copyObj->setObject($this->object);

		$copyObj->setMetaTitle($this->meta_title);

		$copyObj->setMetaDescription($this->meta_description);

		$copyObj->setMetaKeywords($this->meta_keywords);

		$copyObj->setJavascript($this->javascript);

		$copyObj->setManualkeyword($this->manualkeyword);

		$copyObj->setExcludesitemap($this->excludesitemap);

		$copyObj->setExcludecrawler($this->excludecrawler);

		$copyObj->setPriority($this->priority);


		$copyObj->setNew(true);

		$copyObj->setId(NULL); // this is a auto-increment column, so set to default value

	}

	/**
	 * Makes a copy of this object that will be inserted as a new row in table when saved.
	 * It creates a new object filling in the simple attributes, but skipping any primary
	 * keys that are defined for the table.
	 *
	 * If desired, this method can also make copies of all associated (fkey referrers)
	 * objects.
	 *
	 * @param      boolean $deepCopy Whether to also copy all rows that refer (by fkey) to the current row.
	 * @return     Properties Clone of current object.
	 * @throws     PropelException
	 */
	public function copy($deepCopy = false)
	{
		// we use get_class(), because this might be a subclass
		$clazz = get_class($this);
		$copyObj = new $clazz();
		$this->copyInto($copyObj, $deepCopy);
		return $copyObj;
	}

	/**
	 * Returns a peer instance associated with this om.
	 *
	 * Since Peer classes are not to have any instance attributes, this method returns the
	 * same instance for all member of this class. The method could therefore
	 * be static, but this would prevent one from overriding the behavior.
	 *
	 * @return     PropertiesPeer
	 */
	public function getPeer()
	{
		if (self::$peer === null) {
			self::$peer = new PropertiesPeer();
		}
		return self::$peer;
	}

