					$o->clearAllReferences($deep);
				}
			}
			if ($this->collResources) {
				foreach ((array) $this->collResources as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collCustomerSettings) {
				foreach ((array) $this->collCustomerSettings as $o) {
					$o->clearAllReferences($deep);
				}
			}
		} // if ($deep)

		$this->collPartners = null;
		$this->collCustomers = null;
		$this->collConsumers = null;
		$this->collResources = null;
		$this->collCustomerSettings = null;
	}

	// symfony_behaviors behavior
	
	/**
	 * Calls methods defined via {@link sfMixer}.
	 */
	public function __call($method, $arguments)
	{
	  if (!$callable = sfMixer::getCallable('BaseUser:'.$method))
	  {
	    throw new sfException(sprintf('Call to undefined method BaseUser::%s', $method));
	  }
	
	  array_unshift($arguments, $this);
	
	  return call_user_func_array($callable, $arguments);
	}

} // BaseUser
