	  if (!isset($this->current_i18n[$culture]))
	  {
	    $object = $this->isNew() ? null : LastminuteI18NPeer::retrieveByPK($this->getPrimaryKey(), $culture);
	    if ($object)
	    {
	      $this->setLastminuteI18NForCulture($object, $culture);
	    }
	    else
	    {
	      $this->setLastminuteI18NForCulture(new LastminuteI18N(), $culture);
	      $this->current_i18n[$culture]->setCulture($culture);
	    }
	  }
	
	  return $this->current_i18n[$culture];
	}
	
	/**
	 * Sets the translation object for a culture.
	 */
	public function setLastminuteI18NForCulture(LastminuteI18N $object, $culture)
	{
	  $this->current_i18n[$culture] = $object;
	  $this->addLastminuteI18N($object);
	}

} // BaseLastminute
