	/**
	 * Build a Criteria object containing the values of all modified columns in this object.
	 *
	 * @return     Criteria The Criteria object containing all modified values.
	 */
	public function buildCriteria()
	{
		$criteria = new Criteria(InvoicePeer::DATABASE_NAME);

		if ($this->isColumnModified(InvoicePeer::STATUS)) $criteria->add(InvoicePeer::STATUS, $this->status);
		if ($this->isColumnModified(InvoicePeer::COMPANY_ID)) $criteria->add(InvoicePeer::COMPANY_ID, $this->company_id);
		if ($this->isColumnModified(InvoicePeer::TITLE)) $criteria->add(InvoicePeer::TITLE, $this->title);
		if ($this->isColumnModified(InvoicePeer::CUSTOMER)) $criteria->add(InvoicePeer::CUSTOMER, $this->customer);
		if ($this->isColumnModified(InvoicePeer::EMAIL)) $criteria->add(InvoicePeer::EMAIL, $this->email);
		if ($this->isColumnModified(InvoicePeer::DATE)) $criteria->add(InvoicePeer::DATE, $this->date);
		if ($this->isColumnModified(InvoicePeer::ENDDATE)) $criteria->add(InvoicePeer::ENDDATE, $this->enddate);
		if ($this->isColumnModified(InvoicePeer::PAYEDDATE)) $criteria->add(InvoicePeer::PAYEDDATE, $this->payeddate);
		if ($this->isColumnModified(InvoicePeer::PAYMETHOD)) $criteria->add(InvoicePeer::PAYMETHOD, $this->paymethod);
		if ($this->isColumnModified(InvoicePeer::TRANSACTIONCODE)) $criteria->add(InvoicePeer::TRANSACTIONCODE, $this->transactioncode);
		if ($this->isColumnModified(InvoicePeer::ROWS)) $criteria->add(InvoicePeer::ROWS, $this->rows);
		if ($this->isColumnModified(InvoicePeer::ID)) $criteria->add(InvoicePeer::ID, $this->id);

		return $criteria;
	}

