	public function copyInto($copyObj, $deepCopy = false)
	{

		$copyObj->setStatus($this->status);

		$copyObj->setCompanyId($this->company_id);

		$copyObj->setTitle($this->title);

		$copyObj->setCustomer($this->customer);

		$copyObj->setEmail($this->email);

		$copyObj->setDate($this->date);

		$copyObj->setEnddate($this->enddate);

		$copyObj->setPayeddate($this->payeddate);

		$copyObj->setPaymethod($this->paymethod);

		$copyObj->setTransactioncode($this->transactioncode);

		$copyObj->setRows($this->rows);


		$copyObj->setNew(true);

		$copyObj->setId(NULL); // this is a auto-increment column, so set to default value

